$("#sign-in").click(function(e){e.preventDefault();var a=$(this);a.button("loading");var r={UserName:$("#username").val(),Password:$("#password").val(),RememberMe:!0};$.ajax({type:"POST",url:"/api/session",contentType:"application/json;charset=utf-8",data:JSON.stringify(r)}).success(function(e){location.reload()}).fail(function(e){a.button("reset");for(var r=JSON.parse(e.responseText).errors,t=0;t<r.length;t++)new Noty({type:"error",text:r[t].key+": "+r[t].value[0]}).show()})}),$("#logout").click(function(e){e.preventDefault(),$.ajax({type:"DELETE",url:"/api/session",contentType:"application/json;charset=utf-8"}).success(function(e){window.location.replace("/")})}),$("#signup").click(function(e){e.preventDefault();var a={UserName:$("#username").val(),Password:$("#password").val(),Name:$("#name").val(),ConfirmPassword:$("#confirmpassword").val(),Surname:$("#surname").val(),Email:$("#email").val(),Image:$(".cloudinary-fileupload").attr("value"),PhoneNumber:$("#phone").val(),Country:$("#country").val(),City:$("#city").val(),CreditCard:$("#credit").val()};$.ajax({type:"POST",url:"/api/users",contentType:"application/json;charset=utf-8",data:JSON.stringify(a)}).success(function(e){new Noty({type:"success",text:"All is good! Now you can login by your username and password"}).show()}).fail(function(e){if(e.responseJSON.errors)for(var a=e.responseJSON.errors,r=0;r<a.length;r++)new Noty({type:"error",text:a[r].key+": "+a[r].value[0]}).show();else var a=e.responseJSON;for(var r=0;r<a.length;r++)new Noty({type:"error",text:a[r].description}).show()})});